 import { Button } from "bootstrap"
import { Stack } from "react-bootstrap"
import { useShopingCard } from "../../context/Shopping"
import DateProduct from '../../Data/DateProduct'
import Bar from "../../HookCustom/FilterNumbr" 

const CardItem = ({id , quentity}) => {
    const {removeItem} = useShopingCard()
    const item = DateProduct.find((i)=>i.id === id)
    if(item == null) return null;
  return (
   <Stack direction = 'horizontal' gab={2} className="d-flex align-items-center">
    <img src={item.img} alt="card-imag"
    style={{width:'125px' , height:'75px', objectFit:"cover"}}
    />

    <div className="me-auto">
    <div>
    {item.name}{" "}
    {quentity > 1 && <span className="text-muted" style={{fontSize:'0.65rem'}}>X{quentity}</span>}
    </div>
      <div className="text-metud" style={{fontSize:'0.75rem'}} >
      { Bar(item.price)}
      </div>

      <div>
      { Bar(item.price * quentity)}

      </div>
    </div>
    <Button variant='outline-danger' size="sm" onClick={()=>removeItem(id)}>&times;</Button>
   </Stack>
  )
}

export default CardItem
