 import './App.css';
import { Navbar,Hero,Product } from './section/Index';
import { Container } from './component/Index';
import DateProduct from './Data/DateProduct';
import { useState } from 'react';

function App() {

  const [DataP , setDataP] = useState(DateProduct)

//Function by Filter Category

const FilterCategory = (cat) =>{

  const newArray = DataP.filter((item)=>item.category == cat)

  setDataP(newArray)

}


const ForCategory = ['All' , ...new Set(DataP.map((i)=>i.category))]






  return (
    <>

     <Navbar/>

      <Container>
      <Hero/>
      <Product DataP = {DataP}  FilterCategory = {FilterCategory}/>


      </Container>


    </>
  );
}

export default App;
